import React, { useState } from 'react';
import './Authentication.css';
import { Link } from 'react-router-dom';
import { useSelector } from 'react-redux/es/hooks/useSelector';
import { login } from '../../actions/UserActions';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';

const Login = () => {
    const {loading}=useSelector((state)=>state.user);
    const [email,setEmail]=useState();
    const [password,setPassword]=useState();
    const dispatch=useDispatch();
    const navigate=useNavigate();

    const handleEmail=(e)=>{
      setEmail(e.target.value);
    }
    const handlePassword=(e)=>{
        setPassword(e.target.value);
    }

    const validateLoginForm = (email,password) => {
        if (email === "") {
          toast.error("Email and Password is required.", toastOptions);
          return false;
        } else if (password === "") {
          toast.error("Email and Password is required.", toastOptions);
          return false;
        }
        return true;
      };

    const handleLogin=(e)=>{
        e.preventDefault();
        if(validateLoginForm(email,password)){
            dispatch(login(email,password));
            navigate('/');
        }
        
    }
  return (
    <div className='authentication-box'>
    <div className='authentication-item'>
    <div className='auth-icon'>
    <i class="fa fa-user" aria-hidden='true'></i> 
    </div>
    <div className='auth-input'>
    <input type='email'
    value={email}
    onChange={handleEmail}
    disabled={loading}
    placeholder='Email Address'
     />

    </div>
    

    </div>
    <div className='authentication-item'>
       <div className='auth-icon'>
          <i class="fa fa-lock" aria-hidden='true'></i>
       </div>
       <div>
       <input type='password'
        value={password}
        onChange={handlePassword}
        placeholder='Password'
        disabled={loading}
       />
       </div>
        

    </div>
    <div className='authentication-button'>
        <button className='login-button' type='submit' onClick={handleLogin}>Log in</button>

    </div>
    <div className='authentication'>
        <Link className='authentication-link' > Forgotten Password</Link>
    </div>

    <div className='authentication-bottom'>
        <button className='register-button' type='submit' >create new account</button>

    </div>

    </div>
  )
}

export default Login;
