import React, { useEffect, useState } from 'react';
import './Userchat.css';
import axios from 'axios';
import { server } from '../../store';
import io from 'socket.io-client';

var socket;

const Userchat = (props) => {

  
  const ENDPOINT='http://localhost:4000';
  const [message,setMessage]=useState([]);
  const [newMessage,setNewMessage]=useState("");
  const [chats,setChats]=useState();
  const [socketConnected,setSocketConnected]=useState();
  const [typing ,setTyping]=useState(false);
  const chatId=props.chatId;
  const userAccessToken=localStorage.getItem('userAccessToken');
  const finItem=JSON.parse(userAccessToken);

  const handleGetMessages=async (chatID)=>{
    try{
      const {data}=await axios.post(`${server}/getmessage`,{
        chatID
      });
      if(data.success){
        setMessage(data.messages);
        socket.emit("join chat",chatID);
      }
      else{
  
      }
    }
    catch(error){
      console.log(error);
    }
  }

  if(message){
    message.map((m)=>{
      console.log(m.sender,m.content);
    })
  }

  const sendMessage=async (event)=>{
    if (newMessage&&chats) {
      const chatID=chats._id;
      socket.emit("stop typing", chatID);
      try {
        setNewMessage("");
        const { data } = await axios.post(
          `${server}/sendmessage`,
          {
            content: newMessage,
            chatID,
            finItem
          }
        );
        
        setMessage([...message, data.message]);
      } catch (error) {
        console.log(error);
      }
    }
  }


  

  useEffect(() => {
    socket = io(ENDPOINT);
    socket.emit("setup",props.userId);
    socket.on("connected", () => setSocketConnected(true));
    socket.on("typing", () => setTyping(true));
    socket.on("stop typing", () => setTyping(false));

    // eslint-disable-next-line
  }, []);



  useEffect( async ()=> {
    try{
      const {data}= await  axios.post(`${server}/getchat`,{
        chatId,finItem
  });

      if(data.success){
        setChats(data.FullChat);
        const chatID=data.FullChat._id;
        handleGetMessages(chatID);
       
      }
      else{
        
      }

    }
    catch(error){
      console.log(error);
    }
  },[]);


  const typingHandler=(e)=>{

    setNewMessage(e.target.value);

    if (!socketConnected) return;

    if (!typing) {
      console.log('typing');
      setTyping(true);
      socket.emit("typing", chatId);
    }
    let lastTypingTime = new Date().getTime();
    var timerLength = 3000;
    setTimeout(() => {
      var timeNow = new Date().getTime();
      var timeDiff = timeNow - lastTypingTime;
      if (timeDiff >= timerLength && typing) {
        socket.emit("stop typing", chatId);
        setTyping(false);
      }
    }, timerLength);

  }
 
  return (<>{!chats?"":
   <div className='chatting-body'>
    {sender.name}and {reciever.name}
   
    <div className='chat-input'>
    
    <input type='text' value={newMessage} onChange={typingHandler}/>
    
    
  
    <button type='button' onClick={sendMessage}>Send</button>
    

    </div>
        
    </div>
   

  }</>
   
  )
}

export default Userchat;
