import React, { useEffect, useState } from 'react';
import axios from 'axios';
import PostCard from './PostCard';
import './Middlewar.css';

const Middlebar = () => {
  const [pics,setPics]=useState([]);
  const postCreate=(pic)=>{
    const date=pic.createdAt.split('T');
    const tarikh=date[0].split('-');
    const time=date[1].split(':');
   console.log(tarikh,time);
    return(
      <PostCard name={pic.name} userpic={pic.profilepic} postImage={pic.image} index={pic._id} description={pic.description}/>
    )
  }
  useEffect( ()=>{
     axios.get('http://localhost:4000/api/s1/getposts')
     .then(res => { setPics(res.data.post); })
     .catch(err => { console.log(err); });
  });
  var size=pics.length;
  var run=size>0;

  return (<>
  {!run 
  ?<div></div>
   :<div className='middle-bar'>Middlebar
  {pics.map(postCreate)}
  </div>
  }
    
</>  )
}

export default Middlebar;
