import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import './Postcard.css';


const PostCard = (props) => {
  const [liked,setLiked]=useState(false);
    const handleLike=(index)=>{
          console.log(index);
          setLiked(true);
          document.getElementById(index).style.color='blue';
    }


    var path='http://localhost:4000/Images/'+props.postImage;
    var userpath='http://localhost:4000/Images/'+props.userpic;
    
  return (
    <div className='p-c-container'>

    <div className='p-c-box'>
     <div className='pc-item'>
        <img className='p-c-pic' src={userpath} /><span>{props.name}</span> <span>{props.tarikh} created at{props.time}</span>
     </div>
     <div className='pc-item'>
        <p>{props.description}</p>

     </div>
     <div className='pc-item'>
        <img className='p-c-img' src={path}/>
     </div>
     <div className='pc-item'>
     <span className='like-button'  onClick={()=>handleLike(props.index)}>
     <i id={props.index} class="fa fa-thumbs-o-up" aria-hidden='true'></i>
     </span>
      
     </div>

    </div>
    </div>
  )
}

export default PostCard;
