import React, { useEffect, useState } from 'react';
import './Userchat.css';
import axios from 'axios';
import { server } from '../../store';
import io from 'socket.io-client';
import { useSelector } from 'react-redux';
var socket;

const Userchat = (props) => {

  
  const ENDPOINT='http://localhost:4000';
  const [message,setMessage]=useState();
  const [chats,setChats]=useState();
  const [sender,setSender]=useState();
  const [reciever,setReciever]=useState();
  const [socketConnected,setSocketConnected]=useState();
  const [isTyping ,setIsTyping]=useState();
  const chatId=props.chatId;
  const userAccessToken=localStorage.getItem('userAccessToken');
  const finItem=JSON.parse(userAccessToken);

  const handleGetMessages=async (chatID)=>{
    try{
      const {data}=await axios.post(`${server}/getmessage`,{
        chatID
      });
      if(data.success){
        console.log(data.messages);
        socket.emit("join chat",chatId);
      }
      else{
  
      }

    }
    catch(error){
      console.log(error);
    }
    


  }

  const sendMessage=(e)=>{
    if(e.key=="Enter"){
      console.log('message sent');
    }
    
  }


  

  useEffect(() => {
    socket = io(ENDPOINT);
    socket.emit("setup",props.userId);
    socket.on("connected", () => setSocketConnected(true));
    socket.on("typing", () => setIsTyping(true));
    socket.on("stop typing", () => setIsTyping(false));

    // eslint-disable-next-line
  }, []);

  useEffect( async ()=> {
    try{
      const {data}= await  axios.post(`${server}/getchat`,{
        chatId,finItem
  });

      if(data.success){
        setChats(data.FullChat);
        setSender(data.sender);
        setReciever(data.reciever);
        const chatID=data.FullChat._id;
        handleGetMessages(chatID);
       
      }
      else{
        
      }

    }
    catch(error){
      console.log(error);
    }
  },[]);
 
  return (<>{!sender?"":
   <div className='chatting-body'>
    {sender.name}and {reciever.name}
   
    <div className='chat-input'>
    <form onSubmit={sendMessage}>
    <input type='text' onChange={(e)=>{(setMessage(e.target.value))}}/>
    </form>
    
    
  
    <button type='button' onKeyDown={sendMessage}>Send</button>
    

    </div>
        
    </div>
   

  }</>
   
  )
}

export default Userchat;
