import React, { useState } from 'react';
import { useSelector } from 'react-redux';
import './Postcard.css';


const PostCard = (props) => {
  const [liked,setLiked]=useState(false);
    const handleLike=(index)=>{
          console.log(index);
          setLiked(true);
          document.getElementById('like-button').style.color='blue';
    }
    var path='http://localhost:4000/Images/'+props.postImage;
    var userpath='http://localhost:4000/Images/'+props.userpic;
    
  return (
    <div className='p-c-container'>

    <div className='p-c-box'>
     <div className='pc-item'>
        <img className='p-c-pic' src={userpath} /><span>{props.name}</span> 
     </div>
     <div className='pc-item'>
        <img className='p-c-img' src={path}/>
     </div>
     <div className='pc-item'>
     <span index={props.index} onClick={()=>handleLike(props.index)}>
     <i id='like-button'class="fa fa-thumbs-o-up" aria-hidden='true'></i>
     </span>
      
     </div>

    </div>
    </div>
  )
}

export default PostCard;
