import Header from '../../Components/Header';

const Post = () => {
  return (
    <>
    <Header/>
      <div className='post-box'>
      <div className='post-item'>
          Create Post
      </div>
      <div className='post-item'>
          <input type='file'
            onChange={handleFile}

          />
      </div>
      <div className='post-item'>
         <input type='text'
            placeholder='Description'
            onChange={(e)=>{setDescription(e.target.value)}}
            value={description}
         />

      </div>
      <div className='post-item'>
         <button type='button' onClick={handlePost}>

         </button>
      </div>

      </div>
    </>
    
  )
}

export default Post;
