import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Middlewar.css';
import { useSelector } from 'react-redux';

const Middlebar = () => {
  const {post}=useSelector((state)=>state.user);
  const [pics,setPics]=useState({});
  useEffect(async ()=>{
    const {data}=await axios.post('http://localhost:4000/api/s1/getposts');
    setPics(data.post);
  })
  return (
    <div className='middle-bar'>Middlebar
    <img src={'http://localhost:4000/Images/'+post[0].image}/>
    </div>
  )
}

export default Middlebar;
