import React,{useState} from 'react';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { register } from '../../actions/UserActions';
import toast,{Toaster} from 'react-hot-toast';
import { formToJSON } from 'axios';


const Register = () => {
  const [name,setName]=useState('');
  const [email,setEmail]=useState('');
  const [password,setPassword]=useState('');
  const [date,setDate]=useState('');
  const [image,setImage]=useState('');
  const dispatch=useDispatch();
  const navigate=useNavigate();
  const registerValidation = (email,password,confirmPassword) => {
    if (email === ""||password===""||confirmPassword==="") {
      toast.error("Please enter all fields.");
      return false;
    }
    else if (password.length < 6) {
      toast.error(
        "Password should be equal or greater than 6 characters."
      );
      return false;
    } 
    

    return true;
  };

  const handleImage = (e) => {
    console.log(e.target.files);
      setImage(e.target.files[0]);
   }

  const handleSubmit=(e)=>{
    e.preventDefault();
    if(registerValidation(name,email,password)){
      const formData=new FormData();
      formData.append("image",image);
      formData.append("name",name);
      formData.append("email",email);
      formData.append("password",password);
      formData.append("date",date);
      dispatch(register(formData));
      navigate('/');
    }
  }

 
  return(
    <div className='register-background'>
      <div className='register-box'>
         <div className='register-item'>
           <h1>Socialbook</h1>
         </div>
         <div className='register-item'>
            <input className='authentication-input'
            placeholder='Name'
            value={name}
            onChange={(e)=>{setName(e.target.value)}}
            type='text'/>
         </div>
         <div className='register-item'>
           <input className='authentication-input'
           placeholder='Email'
           value={email}
           onChange={(e)=>{setEmail(e.target.value)}}
            type='email'/>
         </div>
         <div className='register-item'>
            <input className='authentication-input' 
            placeholder='Password'
            value={password}
            onChange={(e)=>{setPassword(e.target.value)}}
            type='password'/>
         </div>
         <div className='register-item'>
           <span>Date Of Birth:</span><input className='authentication-input'
           value={date}
           onChange={(e)=>{setDate(e.target.value)}} 
           type='date' />
         </div>
         <div className='register-item'>
         <span>Gender:</span>
          <select>
            <option >Male </option>
            <option >Female</option>
            <option >Custom</option>
          </select>
            </div>
            <div className='register-item'>
            <input type='file' onChange={handleImage}/>

            </div>
         <div className='register-item'>
           <button type='button' onClick={handleSubmit}> Sign Up</button>
         </div>

      </div>
    <Toaster toastOptions={{duration:5000}}/>
    </div>
  )
}

export default Register;
